import * as express from 'express';
import 'express-async-errors';
import router from './routes';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();

    this.config();

    this.routes();

    // Não remover essa rota
    this.app.get('/', (req, res) => res.json({ ok: true }));
  }

  private config():void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(express.json());
    this.app.use(accessControl);
  }

  public start(PORT: string | number): void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }

  private routes(): void {
    this.app.use(router);
  }
}

export { App };

// Essa segunda exportação é estratégica, e a execução dos testes de cobertura depende dela
export const { app } = new App();

import { Router } from 'express';
import drinksRouter from './drinks.routes';
import mealsRouter from "./meals.routes";
import userRouter from './user.routes';
import profileRouter from './profile.routes'

const router = Router();

router.use('/user', userRouter);
router.use('/drinks', drinksRouter);
router.use('/meals', mealsRouter);
router.use('/profile', profileRouter)

export default router;

import { Request, Router, Response } from 'express';
import DrinkController from '../controllers/DrinksController';
import Authorization from '../middlewares/auth.middlware';
import DrinksController from '../controllers/DrinksController';

const drinkController = new DrinkController();

const drinksRouter = Router();

drinksRouter.get(
  '/name',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getAllDrinks(req, res)
);

drinksRouter.get(
  '/letter',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getDrinksByFirstLetter(req, res)
);

drinksRouter.get(
  '/category',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getDrinkByCategory(req, res)
);

drinksRouter.get(
  '/categories',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getAllCategories(req, res)
);

drinksRouter.get(
  '/random',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getRandomDrink(req, res)
);

drinksRouter.get(
  '/ingredients',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getAllIngredients(req, res)
);

drinksRouter.get(
  '/ingredient',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getByIngredient(req, res)
);

drinksRouter.get(
  '/:id',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.getById(req, res)
);

drinksRouter.post(
  '/inprogress',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.addDrinkInProgress(req, res)
);

drinksRouter.get(
  '/inprogress/:id',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.findRecipeInProgressById(req, res)
);
drinksRouter.patch(
  '/inprogress/:id',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.updateRecipeInProgressById(req, res)
);
drinksRouter.get(
  '/favorites/search',

  (req: Request, res: Response) => drinkController.getFavoriteRecipes(req, res)
);
drinksRouter.post(
  '/favorites/:id',
  Authorization.auth,
  (req: Request, res: Response) => drinkController.favoriteDrinkRecipe(req, res)
);
drinksRouter.post(
  '/donerecipes/:id',

  (req: Request, res: Response) => drinkController.addDoneDrink(req, res)
);
drinksRouter.get(
  '/donerecipes/search',

  (req: Request, res: Response) => drinkController.getDoneRecipes(req, res)
);

export default drinksRouter;

import { Request, Response } from 'express';
import mapStatusHTTP from '../utils/mapStatusHTTP';
import DrinkService from '../services/DrinksService';

export default class DrinksController {
  constructor(
    private drinkService = new DrinkService(),
  ) { }

  async getAllDrinks(req: Request, res: Response) {
    const { q } = req.query;
    if (!q) {
      const { status, data } = await this.drinkService.getDrinks();
      return res.status(mapStatusHTTP(status)).json(data);
    }

    const { status, data } = await this.drinkService.getDrinkByName(q as string);
    return res.status(mapStatusHTTP(status)).json(data);
  } 

  async getById(req: Request, res: Response) {
    const { id } = req.params;
    const { status, data } = await this.drinkService.getById(Number(id));

    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getDrinksByFirstLetter(req: Request, res: Response) {
    const { q } = req.query;
    const { status, data } = await this.drinkService.getDrinksByFirstLetter(q as string);

    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getAllCategories(_req: Request, res: Response) {
    const { status, data } = await this.drinkService.getAllCategories();
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getDrinkByCategory(req: Request, res: Response) {
    const { q } = req.query;
    const { status, data } = await this.drinkService.getDrinkByCategory(q as string);
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getRandomDrink(req: Request, res: Response) {
    const { status, data } = await this.drinkService.getRandomDrink();
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getByIngredient(req: Request, res: Response) {
    const { q } = req.query;    
      const { status, data } = await this.drinkService.getByIngredients(q as string);
      return res.status(mapStatusHTTP(status)).json(data);    
  }

  async getAllIngredients(_req: Request, res: Response) {    
    const { status, data } = await this.drinkService.getAllIngredients();
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async addDrinkInProgress(req: Request, res: Response) {
    const inProgress = req.body;
    const { status, data } = await this.drinkService.addDrinkInProgress(inProgress);
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async findRecipeInProgressById(req: Request, res: Response) {
    const { id } = req.params;
    const { user } = req.query;
    const { status, data } = await this.drinkService.findRecipeInProgressById({
      userId: Number(user as string),
      drinkId: Number(id),
    });
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async updateRecipeInProgressById(req: Request, res: Response) {
    const { id } = req.params;
    const { user } = req.query;
    const { markedIngredients } = req.body;
    const { status, data } = await this.drinkService.updateRecipeInProgressById({
      userId: Number(user as string),
      drinkId: Number(id),
      markedIngredients,
    })
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async favoriteDrinkRecipe(req: Request, res: Response) {
    const { id } = req.params;
    const { userId } = req.body
    const { status, data } = await this.drinkService.favoriteDrinkRecipe(userId, Number(id));

    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getFavoriteRecipes(req: Request, res: Response) {
    const { user } = req.query
    const { status, data } = await this.drinkService.getFavoriteRecipes(Number(user));
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async addDoneDrink(req: Request, res: Response) {
    const { id } = req.params;
    const { userId } = req.body;   
    const { status, data } = await this.drinkService.addDoneDrink(userId, Number(id));
    return res.status(mapStatusHTTP(status)).json(data);
  }

  async getDoneRecipes(req: Request, res: Response) {
    const { user } = req.query;    
    const { status, data } = await this.drinkService.getDoneRecipes(Number(user));
    return res.status(mapStatusHTTP(status)).json(data);
  }
}

export default function mapStatusHTTP(status: string): number {
  switch (status) {
    case 'SUCCESSFUL': return 200;
    case 'CREATED': return 201;
    case 'INVALID_DATA': return 400;
    case 'UNAUTHORIZED': return 401;
    case 'NOT_FOUND': return 404;
    case 'CONFLICT': return 409;
    case 'UNPROCESSABLE ENTITY': return 422;
    default: return 500;
  }
}

import { ServiceResponse } from '../Interfaces/serviceReponse';
import { iDrinkRecipe } from '../Interfaces/drinks/iDrinks';
import DrinksModel from '../models/Drinks.model';
import { IProgressDrinkRecipe } from '../Interfaces/IProgress';

export default class MatchesService {
  constructor(
    private drinkModel = new DrinksModel(),
  ) { }

  async getDrinks() {
    const drinks = await this.drinkModel.findAll();

    if (drinks === null) {
      return { status: 'NOT_FOUND', data: { message: 'drinks not found' } }
    };

    return { status: 'SUCCESSFUL', data: drinks };
  }

  async getById(id: number) {
    const recipe = await this.drinkModel.getDrinkById(id);
    if (!recipe) {
      return { status: 'NOT_FOUND', data: { message: 'drinks not found' } }
    }

    return { status: 'SUCCESSFUL', data: recipe };
  }

  async getDrinkByName(q: string) {
    const drinks = await this.drinkModel.getFilteredDrinks(q);

    if (drinks === null) {
      return { status: 'NOT_FOUND', data: { message: 'drink not found' } }
    };

    return { status: 'SUCCESSFUL', data: drinks };
  }

  async getDrinksByFirstLetter(q: string) {
    const drinks = await this.drinkModel.getFilteredDrinks(q);

    if (drinks === null) {
      return { status: 'NOT_FOUND', data: { message: 'drink not found' } }
    };

    const response = drinks.filter((drink: iDrinkRecipe) => drink.strDrink.startsWith(q)
      || drink.strDrink.startsWith(q.toUpperCase()));

    return { status: 'SUCCESSFUL', data: response };
  }

  async getDrinkByCategory(q: string) {
    const drinks = await this.drinkModel.getDrinkByCategory(q);
    return { status: 'SUCCESSFUL', data: drinks };
  }

  async getAllCategories() {
    const categories = await this.drinkModel.getCategories();
    return { status: 'SUCCESSFUL', data: categories }
  }

  async getRandomDrink(): Promise<ServiceResponse<iDrinkRecipe>> {
    const recipes = await this.drinkModel.findAll();
    const randomRecipe = recipes[Math.floor(Math.random() * recipes.length) + 1];

    return { status: 'SUCCESSFUL', data: randomRecipe };
  }

  async getAllIngredients() {
    const recipes = await this.drinkModel.getAllIngredients();

    return { status: 'SUCCESSFUL', data: recipes };
  }

  async getByIngredients(q: string) {
    const recipes = await this.drinkModel.getByIngredients(q);

    return { status: 'SUCCESSFUL', data: recipes };
  }

  async addDrinkInProgress(recipeInProgress: Omit<IProgressDrinkRecipe, 'id'>): Promise<ServiceResponse<IProgressDrinkRecipe>> {
    const { userId, drinkId } = recipeInProgress;

    if (!userId) {
      return {status: 'INVALID_DATA', data: { message: 'Must have a userId' } }
    }

    if (!drinkId) {
      return {status: 'INVALID_DATA', data: { message: 'Must have a drinkId' } }
    }
    // IF NEEDS TO CHECK IF RECIPE IS ALREADY IN PROGRESS, INVOKE 'findRecipeInProgressById' HERE:    
    const recipe = await this.drinkModel.addDrinkInProgress(recipeInProgress)   
    return { status: 'SUCCESSFUL', data: recipe }
  }

  async findRecipeInProgressById(recipeInProgress: Omit<IProgressDrinkRecipe, 'id' | 'markedIngredients'>): Promise<ServiceResponse<IProgressDrinkRecipe>>  {
    const { userId, drinkId } = recipeInProgress;
    if (!userId) {
      return {status: 'INVALID_DATA', data: { message: 'Must provide a userId' } }
    };
    if (!drinkId) {
      return {status: 'INVALID_DATA', data: { message: 'Must provide a mealId' } }
    };

    const inProgress = await this.drinkModel.findDrinkInProgress({
      userId,
      drinkId,
    })

    if (!inProgress) {
      return { status: 'NOT_FOUND', data: { message: 'Recipe not found!' } };
    }

    return { status: 'SUCCESSFUL', data: inProgress };
  }

  async updateRecipeInProgressById(inProgress: Omit<IProgressDrinkRecipe, 'id'>) {
    const response = await this.drinkModel.updateMarkedIngredients(inProgress);

    if (response !== 1) {
      return { status: 'NOT_FOUND', data: { message: 'Recipe not found' } };
    };
    return { status: 'SUCCESSFUL', data: { message: `Marked ingredients updated!` } };
  }

  async favoriteDrinkRecipe(userId: number, id: number) {
    const favorite = await this.drinkModel.createFavoriteDrinks(userId, id);
    if(favorite) {
     return {status: 'SUCCESSFUL', data: { message: 'Recipe stored in favorites' }}    
    } 
    return {status: 'SUCCESSFUL', data: { message: 'Recipe removed from favorites' }};
   }

  async getFavoriteRecipes(userId: number) {
    const favoriteRecipes = await this.drinkModel.getFavoriteRecipes(userId);
    if (favoriteRecipes.length === 0) {
      return {status: 'NOT_FOUND', data: { message: 'No favorite recipes stored!' }};
    }

    return { status: 'SUCCESSFUL', data: favoriteRecipes };
  }

  async addDoneDrink(userId: number, id: number) {
    await this.drinkModel.createDoneDrinks(userId, id);
    return { status: 'SUCCESSFUL', data: { message: 'Recipe is done!' } }
  }

  async getDoneRecipes(userId: number) {
    const doneRecipes = await this.drinkModel.getDoneRecipes(userId);    
    if (doneRecipes.length === 0) {
      return {status: 'NOT_FOUND', data: { message: 'No done recipes stored!' }};
    }

    return { status: 'SUCCESSFUL', data: doneRecipes };
  }
}

import { Op } from 'sequelize';
import SequelizeDrinks from '../database/models/03Drinks-Recipes.model';
import DrinkCategories from '../database/models/01Drinks-Categories.model';
import { IDrinkModel, iDrinkCategories, iDrinkRecipe } from '../Interfaces/drinks/iDrinks';
import DrinksCategories from '../database/models/01Drinks-Categories.model';
import InProgressDrinksModel from '../database/models/07In-Progress-Drinks';
import { IProgressDrinkRecipe } from '../Interfaces/IProgress';
import { startDrinkRecipeInProgress } from '../utils/startRecipeInProgress';
import FavoriteDrinksModel from '../database/models/05Favorite-Drinks';
import FinishedDrinksModel from '../database/models/09Finished-Drinks';

export default class DrinksModel implements IDrinkModel {
  private Drinkmodel = SequelizeDrinks;
  private inProgressModel = InProgressDrinksModel;
  private CategoryModel = DrinkCategories;
  private FavoriteDrinksModel = FavoriteDrinksModel;
  private DoneDrinksModel = FinishedDrinksModel;

  async findAll(): Promise<iDrinkRecipe[]> {
    const recipes = await this.Drinkmodel.findAll({
      include: [{
        model: DrinksCategories, as: 'category', attributes: ['strCategory']
      }],
      attributes: { exclude: ['strCategory'] }
    });

    const newRecipes = recipes.map((recipe) => {
      const { category, ...rest } = recipe.toJSON() as iDrinkRecipe;
      return { ...rest, strCategory: category?.strCategory };
    });

    return newRecipes;
  }

  async getDrinkById(id: number) {
    const recipe = await this.Drinkmodel.findByPk(id);
    return recipe;
  }

  async getFilteredDrinks(q: string): Promise<iDrinkRecipe[] | null> {
    const dbResponse = await this.Drinkmodel.findAll({
      where: {
        strDrink: {
          [Op.like]: `%${q}%`
        }
      }
    });

    if (!dbResponse) return null;

    return dbResponse;
  }

  async getDrinkByCategory(q: string): Promise<iDrinkRecipe[]> {
    const recipes = await this.Drinkmodel.findAll({
      include: [{
        model: DrinksCategories, as: 'category', attributes: ['strCategory']
      }],
      attributes: { exclude: ['strCategory'] }
    });

    const newRecipes = recipes.map((recipe) => {
      const { category, ...rest } = recipe.toJSON() as iDrinkRecipe;
      return { ...rest, strCategory: category?.strCategory };
    }).filter((recipe) => recipe.strCategory === q);

    return newRecipes
  }

  async getCategories(): Promise<iDrinkCategories[]> {
    const categories = await this.CategoryModel.findAll();
    return categories;
  }

  async getAllIngredients(): Promise<string[]> {
    const recipes = await this.findAll();
    const uniqueIngredients:any [] = [];
    recipes.forEach((recipe) => {
      for(let i = 1; i <= 20; i += 1) {
        const ingredientKey = `strIngredient${i}` as keyof iDrinkRecipe;
        const ingredient = recipe[ingredientKey];
        if(ingredient) {
          uniqueIngredients.push(ingredient);
        }
      }
    });
    const removedDup = new Set(uniqueIngredients);
    const newIngredients = Array.from(removedDup);
    return newIngredients;
  }

  async getByIngredients(q: string) {   
    const recipes = await this.findAll();
    const recipesFiltred = recipes.filter((recipe) => {
      const values: string[] = Object.values(recipe);

      const valuesLower: string[] = values.map((value) =>{
        return typeof value === 'string'? value.toLowerCase() : value;
      })

      if(valuesLower.includes(q.toLowerCase())) {
        return true;
      }
      return false;
    });

    return recipesFiltred;
  }

  async addDrinkInProgress(recipeInProgress: Omit<IProgressDrinkRecipe, 'id'| 'markedIngredients'>): Promise<IProgressDrinkRecipe> {
    const defaultIngredients = startDrinkRecipeInProgress();

    const { dataValues } = await this.inProgressModel.create({...recipeInProgress, markedIngredients: defaultIngredients});

    return dataValues;
  }

  async findDrinkInProgress(recipeInProgress: Omit<IProgressDrinkRecipe, 'id'|'markedIngredients'>): Promise<IProgressDrinkRecipe | null> {
    const { userId, drinkId } = recipeInProgress;
    const foundRecipe = await this.inProgressModel.findOne({
      where: {
        userId,
        drinkId,
      }
    });

    return foundRecipe
  }

  async updateMarkedIngredients(recipeInProgress: Omit<IProgressDrinkRecipe, 'id'>): Promise<number | null> {
    const { userId, drinkId, markedIngredients } = recipeInProgress;
    const rowCount = await this.inProgressModel.update({markedIngredients}, {
      where: {
        userId,
        drinkId,
      }
    })

    if (rowCount[0] === 0) return null;

    return rowCount[0];
  }

  async createFavoriteDrinks(userId: number, drinkId: number) {   
    const findFavorite = await this.findFavorite(userId, drinkId);
    if (!findFavorite) {
      const { dataValues } = await this.FavoriteDrinksModel.create({userId, drinkId});
      return dataValues;
    }
    await this.FavoriteDrinksModel.destroy({where: {userId, drinkId}});
  }

  async findFavorite(userId: number, drinkId: number) {
    const foundFavorite = await this.FavoriteDrinksModel.findOne({
      where: {
        userId,
        drinkId
      }
    });

    return foundFavorite;
  }

  async getFavoriteRecipes(userId: number) {
    const favorites = await this.FavoriteDrinksModel.findAll({
      where: {userId},
      include: [{
        model: SequelizeDrinks,
        as: 'favoriteRecipes',
        attributes: ['idDrink', 'strDrink', 'strDrinkThumb', 'strAlcoholic']
      }],           
      attributes: {exclude: ['drinkId']},      
    });
    // const mapped = favorites.map((obj) => obj.dataValues.favoriteRecipes)

    return favorites;
  }

  async findDone(userId: number, drinkId: number) {
    const foundDone = await this.DoneDrinksModel.findOne({
      where: {
        userId,
        drinkId
      }
    });

    return foundDone;
  }

  async createDoneDrinks(userId: number, drinkId: number) {   
    const findDone = await this.findDone(userId, drinkId);
    if (!findDone) {
      await this.inProgressModel.destroy({where: {userId, drinkId}});
      const { dataValues } = await this.DoneDrinksModel.create({
        userId,
        drinkId
      });
      return dataValues;
    }
    await this.inProgressModel.destroy({where: {userId, drinkId}});
    return findDone;
  }

  async getDoneRecipes(userId: number) {
    const doneRecipes = await this.DoneDrinksModel.findAll({
      where: {userId},
      include: [{
        model: SequelizeDrinks,
        as: 'finishedRecipes',
        attributes: ['idDrink', 'strDrink', 'strDrinkThumb', 'strAlcoholic']
      }],           
      attributes: {exclude: ['drinkId']},      
    });
    // const mapped = favorites.map((obj) => obj.dataValues.favoriteRecipes)

    return doneRecipes;
  }
}

