import { Request, Response } from 'express';
import UserService from '../services/UserService';
import mapStatusHTTP from '../utils/mapStatusHTTP';

export default class UserController {
  constructor(
    private userService: UserService,
  ) { }

  public async login(req: Request, res: Response) {
    const { data, status } = await this.userService.verifyLogin(req.body);
    const httpStatus = mapStatusHTTP(status);

    return res.status(httpStatus).json(data);
  }

  async createNewUser(req: Request, res: Response) {
    const newUser = req.body;
    const { data, status } = await this.userService.createNewUser(newUser);

    const httpStatus = mapStatusHTTP(status);

    return res.status(httpStatus).json(data);

  }
}

import { ServiceResponse } from '../Interfaces/serviceReponse';
import UserModel from '../models/UserModel';
import IUsers, {IUsersModel} from '../Interfaces/IUsers';
import { Token, Login } from '../Interfaces/Login';
import UserValidation from '../utils/userValidation';

export default class UserService {
  constructor(
    private userModel: IUsersModel = new UserModel(),
    private userValidation = new UserValidation()
  ) { }

  public async verifyLogin(login: Login): Promise<ServiceResponse<Token>> {
    const { email, password } = login;   

    const invalidLogin = this.userValidation.validate(email, password);     

    if(invalidLogin) return invalidLogin;

    const user = await this.userModel.findByEmail(email);

    if (!user) return this.userValidation.invalidStatusResponse('invalid_password');

    const invalidPassword = await this.userValidation.checkPassword(password, user.password);

    if (invalidPassword) return invalidPassword as ServiceResponse<Token>;    

    const token = this.userValidation.tokenBuilder(user.id, user.role, user.email);

    return { status: 'SUCCESSFUL', data: token };
  }

  async createNewUser(
    newUser: Omit<IUsers, 'role' | 'profileImage'>
    ): Promise<ServiceResponse<Token>> {

    const { email, password, username } = newUser;
    const invalidData = this.userValidation.validate(email, password);

    if (invalidData) return invalidData;

    const emailExists = await this.userModel.findByEmail(email);

    if (emailExists) return this.userValidation.invalidStatusResponse('email_exists');

    const usernameExists = await this.userModel.findByUsername(username);

    if (usernameExists) return this.userValidation.invalidStatusResponse('username_exists');

    const hashedPassword = await this.userValidation.encryptPassword(password);

    const encryptedUser = { ...newUser, role: 'user', profileImage: '', password: hashedPassword }

    const userInfo = (await this.userModel.createUser(encryptedUser));

    const token = this.userValidation.tokenBuilder(userInfo.id, userInfo.role, userInfo.email);

    return { status: 'CREATED', data: token };
  } 
}

import IUsers from '../Interfaces/IUsers';
import SequelizeUsers from '../database/models/00UserModel';
import { IUsersModel } from '../Interfaces/IUsers';
import FavoriteDrinksModel from '../database/models/05Favorite-Drinks';
import FavoriteMealsModel from '../database/models/06Favorite-Meals';
import InProgressDrinksModel from '../database/models/07In-Progress-Drinks';
import InProgressMealsModel from '../database/models/08In-Progress-Meals';
import FinishedDrinksModel from '../database/models/09Finished-Drinks';
import FinishedMealsModel from '../database/models/10Finished-Meals';


export default class UserModel implements IUsersModel {
  private model = SequelizeUsers;
  private favoriteDrinks = FavoriteDrinksModel;
  private favoriteMeals = FavoriteMealsModel;
  private inProgressDrinks = InProgressDrinksModel;
  private inProgressMeals = InProgressMealsModel;
  private finishedDrinks = FinishedDrinksModel;
  private finishedMeals = FinishedMealsModel;

  async createUser(newUser: Omit<IUsers, 'id'>): Promise<IUsers> {
    const { dataValues } = await this.model.create(newUser);

    return dataValues;
  }

  async findByEmail(email: string): Promise<IUsers | null> {
    try {
      const user = await this.model.findOne({ where: { email } });
      if (user) {
        return user.dataValues;
      }
    } catch (error: any) {
      console.error(error.message);
    }
    return null;
  }

  async findByUsername(username: string): Promise<IUsers | null> {
    const user = await this.model.findOne({ where: { username } });

    if (user === null) return null;

    return user.dataValues;
  }

  async updateImage(id: number, imageUrl: string): Promise<number | null> {
    const rowCount = await this.model.update(
      { profileImage: imageUrl },
      {
        where: { id },
      }
    );
    if (rowCount[0] === 0) return null;

    return rowCount[0];
  }

  async getUserRecipes(id: number) {
    const favDrinks = await this.favoriteDrinks
      .findAll({ where: { userId: id }});
    const favMeals = await this.favoriteMeals
      .findAll({ where: { userId: id }});
    const inProgressDrinks = await this.inProgressDrinks
      .findAll({ where: { userId: id }});
    const inProgressMeals = await this.inProgressMeals
      .findAll({ where: { userId: id }});
    const finishedDrinks = await this.finishedDrinks
      .findAll({ where: { userId: id }});
    const finishedMeals = await this.finishedMeals
    .findAll({ where: { userId: id }});

    const result = {
      favoritesCount: favDrinks.length + favMeals.length,
      inProgressCount: inProgressDrinks.length + inProgressMeals.length,
      finishedCount: finishedDrinks.length + finishedMeals.length, 
    };

    return result;
  }
}

import * as bcrypt from 'bcryptjs';
import * as jwt from 'jsonwebtoken';
import { ServiceResponse } from '../Interfaces/serviceReponse';
import { Token } from '../Interfaces/Login';

class UserValidation {

  validate(email: string, password: string) {
    const validEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!email || !password) return this.invalidStatusResponse('invalid_data');

    if (!validEmail.test(email)) return this.invalidStatusResponse('invalid_emailOrPassword');

    if (password.length < 6) return this.invalidStatusResponse('invalid_emailOrPassword');

    return false;
  }

  async encryptPassword(password: string): Promise<string> {
    const saltRounds = 10;
    const hashedPassword = await bcrypt.hash(password, saltRounds);

    return hashedPassword;
  }

  async checkPassword(password: string, userPassword: string) {
    const match = await bcrypt.compare(password, userPassword);
    if (!match) return this.invalidStatusResponse('invalid_password');

    return false;
  }

  invalidStatusResponse(status: string): ServiceResponse<Token> {
    if (status === 'invalid_data') return { status: 'INVALID_DATA', data: { message: 'All fields must be filled' } };
    if (status === 'invalid_emailOrPassword') return { status: 'UNAUTHORIZED', data: { message: 'Invalid email or password' } };
    if (status === 'email_exists') return { status: 'INVALID_DATA', data: { message: 'Email already exists' } };
    if (status === 'username_exists') return { status: 'INVALID_DATA', data: { message: 'Username already exists' } };


    return { status: 'UNAUTHORIZED', data: { message: 'Invalid email or password' } };    
  }

  tokenBuilder(sub: number, role: string, email: string): Token {
    const payload = { sub, role, email };

    const secret = process.env.JWT_SECRET ?? 'jwt_secret';

    const token = jwt.sign(payload, secret, { expiresIn: '7d' });

    return { token };
  }
}

export default UserValidation;

